void to_file (const Image img, char *name) {
    FILE *out = fopen (name, "w");
    int maxval = 0;

    for (int i = 0; i < img.height * img.width; ++i)
        maxval = MAX (img.pixels[i], maxval);

    fprintf (out, "P2\n");
    fprintf (out, "%i %i\n", img.width, img.height);
    fprintf (out, "%i\n", maxval);

    for (int y = 0; y < img.height; ++y) {
        for (int x = 0; x < img.width; ++x) {

            u8 pix = img.pixels[y * img.width + x];
            fprintf (out, "%i ", pix);
        }
    }

    fclose (out);
}
