Image binarize (const Image *src) {

    const int size = src->width * src->height;
    Image img = {src->width, src->height, malloc (size  * sizeof (u8))};
    int pix, black = 256 , blank = 0;

    for (int i = 0; i < size; ++i) {
        pix = src->pixels[i];

        black = MIN (black, pix), blank = MAX (blank, pix);
    }

    for (int i = 0; i < size; ++i) {
        pix = src->pixels[i];

        img.pixels[i] = (ABS(pix - black) < ABS(pix - blank)) ? 1 : 0;
    }

    return img;
}

int scan_col (const Image img, const int x) {
    int cnt = 0;

    for (int y = 0; y < img.height; ++y)
        if (img.pixels[y * img.width + x]) cnt++;

    return cnt;
}
int scan_row (const Image img, const int y, const Point lim) {
    int cnt = 0;

    for (int x = lim.x; x < lim.y; ++x)
        if (img.pixels[y * img.width + x] != 0) cnt++;

    return cnt;
}
Rect get_glyph (const Image img, int *it) {

    int x = *it, y = 0;
    Point x_lim, start, end;

    while (scan_col (img, x) == 0 && x <= img.width) x++; // skip col with zeros
    start.x = x_lim.x = x;

    while (scan_col (img, x) != 0 && x <= img.width) x++; // get x to next empty col
    end.x = x_lim.y = x;

    while (scan_row (img, y, x_lim) == 0 && y <= img.height) y++; // skip upper empty lines
    start.y = y;

    y = img.height - 1;
    while (scan_row (img, y, x_lim) == 0 && y >= 0) y--; // skip lower empty lines
    end.y = y;

    *it = x;

    return (Rect){start,end};
}

char recognition (const Image img, Rect lim) {
    char symb;
    int num = 0, dig = 10;
    int tmp;
    int width = lim.b.x - lim.a.x, height = lim.b.y - lim.a.y;
    float sy = 7 / (float)height, sx = 5 / (float)width;

    while (dig-->0) {
        tmp = 0;
        for (int y = 0; y < height; ++y) {
            for (int x = 0; x < width; ++x) {
                int dx = (sx * x), dy = (sy * y);
                int ix = x + lim.a.x, iy = (y + lim.a.y);

                u8 pix = img.pixels[iy * img.width + ix];
                u8 ref = ocr_a[dig][dy * 5 + dx];

                if (pix == ref) tmp++;
            }
        }

        if (tmp > num) {
            num = tmp;
            symb = dig;
        }
    }

    return symb + '0';
}
char *ocr (Image *src) {

    int x = 0;
    char *os = malloc (64 * sizeof (char)), *ptr = os;
    Image img = binarize (src);
    Rect lim;

    while (1) {
        lim = get_glyph (img, &x);
        if (x > img.width) break;

        *ptr++ = recognition (img, lim);
    }
    *ptr = '\0';
    // display (&img);
    return os;
}

